package testpro;

public class Addanarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		int sum=0;
		int[] array={4,6,7,8,};
	
		
		
		for(int num : array)
		{
			
			
			sum=sum +num;
			
			
		}
		
		System.out.println("Sum of array elements is:"+sum);
		
		
	}

}
